from .Algebra import cross, normalize


class Triangle:
    def __init__(self, p1, p2, p3):
        self.p1, self.p2, self.p3 = p1, p2, p3

    def normal(self):
        e1 = self.p2 - self.p1
        e2 = self.p3 - self.p1

        return normalize(cross(e1, e2))


class Quad:
    def __init__(self, p1, p2, p3, p4):
        self.p1, self.p2, self.p3, self.p4 = p1, p2, p3, p4